create database chtdtttbtl;
use chtdtttbtl;

CREATE TABLE `benh` (
  `idbenh` varchar(255) NOT NULL,
  `tenbenh` varchar(100) DEFAULT NULL,
  `nguyennhan` varchar(1000) DEFAULT NULL,
  `loikhuyen` varchar(10000) DEFAULT NULL,
  PRIMARY KEY (`idbenh`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

INSERT INTO `benh` VALUES ('D1','Ngộ độc thực phẩm','Vi khuẩn và virus (Campylobacter jejuni, Salmonella, Ngoại độc tố, Độc tố vi nấm...). Ký sinh trùng. Dị ứng thực phẩm. Các loại thực phẩm thường gây dị ứng là: sữa, trứng, cá, động vật giáp xác, các loại ngũ cốc..... Nấm mốc, độc tố tự nhiên và các chất độc hại. Hoặc xuất phát từ khâu sản xuất, bảo quản, chế biến thực phẩm. Hoặc do chất bảo quản, thuốc bảo vệ thực vật, phụ gia...\n\n','Uống nước nhiều: Duy trì sự hydrat hóa là quan trọng để ngừng mệt mỏi và giúp cơ thể loại bỏ độc tố. Tránh ăn thức ăn không chín hoặc thức ăn không được bảo quản đúng cách để ngăn chặn tái phát ngộ độc. Ăn thức ăn dễ tiêu hóa như cháo, bánh mì, hoặc nước lọc để giảm áp lực lên đường tiêu hóa. Tránh các thực phẩm và thức uống kích thích như cafein, thực phẩm cay nồng, và thức ăn giàu chất béo. Cơ thể cần thời gian để hồi phục. Hãy nghỉ ngơi đủ giấc để cơ thể có thể đối mặt với ngộ độc. Sử dụng thuốc chống nôn: Nếu cần thiết, sử dụng thuốc chống nôn theo sự hướng dẫn của bác sĩ để giảm cảm giác buồn nôn. Tìm kiếm sự chăm sóc y tế: Nếu tình trạng ngộ độc không cải thiện hoặc trở nên nghiêm trọng, hãy tìm sự chăm sóc y tế ngay lập tức. Tránh tự chẩn đoán và tự điều trị. Hãy thảo luận với bác sĩ trước khi sử dụng bất kỳ loại thuốc nào hoặc thực hiện các biện pháp tự nhiên. Theo dõi triệu chứng: Ghi chép về thức ăn bạn ăn và triệu chứng bạn trải qua có thể giúp bác sĩ đánh giá tình hình và đưa ra chẩn đoán chính xác.'),('D2','Hội chứng ruột kích thích','Căng thẳng. Thường ăn những thực phẩm không hợp vệ sinh và đã hỏng. Rối loạn nội tiết tố (sự thay đổi bất thường hormone). Tác dụng phụ khi sử dụng kháng sinh. Loạn khuẩn đường ruột. Tiền sử gia đình có người bị bệnh tiêu hóa','Chế độ ăn giàu chất xơ: Chất xơ có thể giúp kiểm soát triệu chứng. Thức ăn giàu chất xơ bao gồm rau củ, ngũ cốc nguyên hạt, và hạt nguyên liệu đầy đủ. Giảm lượng thực phẩm kích thích: Một số thực phẩm như cà phê, rượu, thực phẩm chứa gluten, và thực phẩm cay nồng có thể kích thích IBS. Uống đủ nước có thể giúp duy trì sự ổn định cho hệ tiêu hóa. Các phương pháp giảm căng thẳng như thiền, yoga, và thậm chí tư vấn tâm lý có thể giúp kiểm soát các triệu chứng của IBS. Hạn chế tối đa bỏ bữa ăn, ăn uống không điều độ. Nên ăn chậm, không nên ăn quá nhanh. Hạn chế ăn các thực phẩm cay nóng nhiều, dầu mỡ và các thực phẩm đóng hộp, chế biến sẵn. Hạn chế rượu bia và đồ uống có gas.'),('D3','Trào ngược dạ dày','Lạm dụng thuốc tây. Thói quen sinh hoạt dùng các chất kích thích và gây nghiện như: cafein, rượu, thuốc lá. Có các bệnh liên quan đến dạ dày. Ăn quá no, ăn nhiều thực phẩm khó tiêu. Thừa cân béo phì. Căng thẳng.','Ăn thành từng bữa nhỏ. Nên ăn thường xuyên hơn là ăn ít bữa lớn đối với người có dấu hiệu trào ngược dạ dày thực quản. Lựa chọn các thực phẩm có tính kiềm, có khả năng trung hòa axit như thực phẩm từ tinh bột (bánh mì, bột yến mạch) hay đạm dễ tiêu. Hạn chế thực phẩm kích thích tăng tiết axit hay kích thích cơ thắt dưới thực quản: hoa quả có hàm lượng axit cao (chanh, cam, dứa...) và ít các sản phẩm từ sữa. Giảm sử dụng các thực phẩm giàu chất béo; thực phẩm chua cay. Không hút thuốc, uống rượu bia, đồ uống có gas, không sử dụng các chất kích thích. Giữ cân nặng hợp lý. Không nằm hoặc lao động ngay sau khi ăn. Thư giãn giảm stress có thể làm giảm triệu chứng trào ngược dạ dày thực quản một cách rõ ràng.'),('D4','Viêm loét dạ dày tá tràng','Thường xuyên hút thuốc lá và uống bia rượu (hoặc các loại nước uống có cồn khác). Căng thẳng thần kinh (stress). Thói quen ăn uống, sinh hoạt không điều độ. Nhiễm vi khuẩn Helicobacter pylori (Vi khuẩn HP). Thường xuyên sử dụng thuốc các loại thuốc có tác dụng giảm đau, kháng viêm. Di truyền.','Người bệnh cần tăng cường bổ sung các thực phẩm có tác dụng bảo vệ niêm mạc dạ dày, chữa lành các vết loét hoặc các thực phẩm có khả năng giúp giảm tiết acid và các thực phẩm giàu vitamin, khoáng chất. Các thực phẩm nên bổ sung bao gồm chuối, cơm, bánh mì, canh hoặc súp, sữa chua, đậu bắp, đặc biệt là các loại rau củ màu đỏ và màu xanh đậm,... Trong quá trình chữa viêm loét dạ dày tá tràng, hãy chia nhỏ các bữa ăn, không ăn quá no vào các bữa chính. Người bệnh loét dạ dày tá tràng uống nước ép táo để dễ tiêu hóa thức ăn, uống nước dừa, nước gừng, trà thảo mộc, hỗn hợp tinh bột nghệ và mật ong. Cần tránh các loại thực phẩm gây tổn thương niêm mạc dạ dày, gây tăng acid dạ dày như trái cây chua, dưa cà muối, các loại đồ uống kích thích như rượu bia,... Chế độ sinh hoạt hợp lý: Tập thể dục thường xuyên 30 phút mỗi ngày với các bài tập nhẹ nhàng. Ngủ nghỉ và làm việc đúng giờ, tránh căng thẳng, stress, mệt mỏi, không nên thức quá khuya. Ăn uống đúng giờ không nên bỏ bữa, tránh các đồ cay nóng,... Nhóm thuốc chống viêm giảm đau thông thường cũng là nguyên nhân gây viêm loét dạ dày tá tràng. Các thuốc nhóm này bao gồm: aspirin, ibuprofen, diclofenac… Trong quá trình điều trị viêm loét dạ dày tá tràng, bạn nên sử dụng các thuốc này ở liều thấp, dùng trong thời gian ngắn theo chỉ định của bác sĩ hoặc dược sĩ.'),('D5','Xuất huyết dạ dày','Loét dạ dày là một trong những nguyên nhân phổ biến nhất gây xuất huyết dạ dày. Loét có thể do nhiều nguyên nhân như sử dụng NSAIDs, nhiễm khuẩn Helicobacter pylori, hoặc tình trạng lâu dài của viêm dạ dày. Nhiễm khuẩn Helicobacter pylori, một loại vi khuẩn gắn kết với niêm mạc dạ dày, có thể tạo điều kiện cho tình trạng viêm nhiễm, dẫn đến xuất huyết. Các bệnh lý liên quan đến máu, như thiếu máu hay các bệnh lý máu khác, cũng có thể gây ra xuất huyết dạ dày. Ung thư dạ dày, đặc biệt là các loại ung thư dạ dày, có thể làm tổn thương các mạch máu và dẫn đến tình trạng xuất huyết. Sử dụng rượu và thuốc lá không chỉ tăng nguy cơ mắc bệnh viêm dạ dày mà còn có thể làm tổn thương niêm mạc và gây xuất huyết dạ dày. Những thói quen này đóng góp vào tình trạng lão hóa của niêm mạc dạ dày và làm tăng nguy cơ xuất huyết trong khu vực này.','Hạn chế hoặc tránh các thực phẩm và đồ uống có thể kích thích dạ dày như thực phẩm cay nồng, chua, cà phê, rượu, và các loại thực phẩm giàu chất béo. Ăn nhỏ giọt và thường xuyên hơn cũng có thể giúp giảm áp lực lên dạ dày. Tránh sử dụng thuốc NSAIDs mà không được bác sĩ kê đơn: Nếu có thể, hạn chế sử dụng các loại thuốc không steroid chống viêm (NSAIDs) như aspirin và ibuprofen, trừ khi bác sĩ chỉ định. Học kỹ thuật quản lý căng thẳng như thiền, yoga, hoặc tập luyện nhẹ để giảm bớt căng thẳng, vì căng thẳng có thể gây tăng sản xuất axit dạ dày. Duy trì một lối sống lành mạnh bao gồm việc duy trì cân nặng lành mạnh, tập thể dục đều đặn, và ngủ đủ giấc. Hạn chế hoặc tránh thuốc lá và rượu. Theo dõi triệu chứng và thăm bác sĩ đều đặn để theo dõi tình trạng sức khỏe và nhận sự hỗ trợ chuyên nghiệp khi cần thiết. Những biện pháp này giúp duy trì niêm mạc dạ dày khỏe mạnh và giảm nguy cơ xuất huyết dạ dày.'),('D6','Viêm hang vị dạ dày','Nhiễm khuẩn Helicobacter pylori, tăng axit dạ dày, thiếu máu, căng thẳng tâm lý, hút thuốc lá, và việc ăn quá nhiều thức ăn cay nồng, chua, hoặc thực phẩm giàu chất béo có thể gây kích thích và tổn thương niêm mạc dạ dày. ','Hạn chế hoặc tránh các thực phẩm và đồ uống có thể kích thích dạ dày, như thực phẩm cay nồng, chua, cà phê, và cacao. Ăn ít và thường xuyên hơn thay vì ăn nhiều bữa lớn. Tránh ăn trước khi đi ngủ. Giảm cân nếu bạn đang ở trạng thái thừa cân, vì cân nặng thừa có thể gây áp lực lên dạ dày. Đảm bảo bạn đang ngủ đủ giấc và giữ tư duy tích cực trước khi đi ngủ. Tránh thuốc lá và hạn chế rượu bia. Học kỹ thuật quản lý căng thẳng như thiền, yoga, hoặc tập luyện nhẹ để giảm bớt căng thẳng. Uống nước trong suốt ngày để giữ cho niêm mạc dạ dày không bị khô và giúp giảm cảm giác nôn. Theo dõi triệu chứng và thăm bác sĩ đều đặn.');

CREATE TABLE `trieuchung` (
  `idtrieuchung` varchar(255) NOT NULL,
  `tentrieuchung` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`idtrieuchung`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

INSERT INTO `trieuchung` VALUES ('S01','Đau bụng'),('S02','Đau quặn theo từng cơn'),('S03','Đau âm ỉ'),('S04','Đau dữ dội, quằn quại'),('S05','Đau nhói hoặc đau rát'),('S06','Đau vùng trên xương chậu và bên dưới xương sườn'),('S07','Đau vùng thượng vị'),('S08','Đau bụng với tần suất 1 lần trong tuần vào kéo dài'),('S09','Đau bụng sau ăn 2 đến 5 tiếng'),('S10','Đau bụng về khuya trong khoảng 1 - 2 giờ sáng'),('S11','Đau khi đói'),('S12','Đau vài phút hoặc đến vài giờ, có tính chu kỳ'),('S13','Buồn nôn, nôn mửa'),('S14','Nôn mửa đi kèm cảm giác mất cân bằng, chóng mặt'),('S15','Nôn sau khi ăn'),('S16','Nôn ra máu tươi'),('S17','Nôn ra máu đen'),('S18','Nôn ra dịch màu (có vị đắng do là dịch mật)'),('S19','Táo bón khoảng 2 đến 3 lần trong tuần'),('S20','Tiêu chảy khoảng 3-4 lần/ngày'),('S21','Tiêu chảy không ngừng'),('S22','Trung tiện nhiều, cảm giác đi tiêu không hết phân'),('S23','Đi cầu phân đen'),('S24','Đi ngoài ra máu'),('S25','Màu phân thay đổi chuyển sang màu đen hoặc màu khác'),('S26','Khó tiêu, chướng bụng, căng bụng'),('S27','Ợ hơi, ợ nóng, ợ chua'),('S28','Dấu hiệu mất nước: môi khô, mắt trũng, khát nước, mạch nhanh, thở nhanh'),('S29','Đau tức ngực'),('S30','Chán ăn'),('S31','Khó nuốt'),('S32','Khàn giọng và ho'),('S33','Miệng tiết ra nhiều nước bọt'),('S34','Đắng miệng'),('S35','Đầy bụng, khó tiêu'),('S36','Hoa mắt, chóng mặt, tụt huyết áp, suy nhược, mệt mỏi, da tái nhợt và thiếu sức sống'),('S37','Cảm giác nuốt nghẹn kéo dài ở cổ họng');

