CREATE TABLE `suydien` (
  `idsuydien` int NOT NULL AUTO_INCREMENT,
  `idluat` varchar(255) DEFAULT NULL,
  `idtrieuchung` varchar(255) DEFAULT NULL,
  `idbenh` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`idsuydien`),
  KEY `idluat` (`idluat`),
  KEY `idtrieuchung` (`idtrieuchung`),
  KEY `idbenh` (`idbenh`),
  CONSTRAINT `suydien_ibfk_1` FOREIGN KEY (`idluat`) REFERENCES `luat` (`idluat`),
  CONSTRAINT `suydien_ibfk_2` FOREIGN KEY (`idtrieuchung`) REFERENCES `trieuchung` (`idtrieuchung`),
  CONSTRAINT `suydien_ibfk_3` FOREIGN KEY (`idbenh`) REFERENCES `benh` (`idbenh`)
